import React from "react";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Container from "react-bootstrap/Container";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const Header = () => {
    const navigate = useNavigate();
    const authToken = localStorage.getItem("access_token");
    const user = JSON.parse(localStorage.getItem("user"));

    const handleLogout = () => {
        localStorage.removeItem("access_token");
        localStorage.removeItem("user");
        toast.success("Logged out successfully!");
        navigate("/login");
    };

    return (
        <header>
            <Navbar
                collapseOnSelect
                expand="lg"
                className="bg-primary navbar-dark shadow-sm"
                style={{
                    position: "fixed",
                    top: 0,
                    width: "100%",
                    zIndex: 1000,
                }}
            >
                <Container>
                    <Navbar.Brand href="/" className="text-white">
                        My Application
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="me-auto">
                            {authToken && user && (
                                <>
                                    <Nav.Link href="/dashboard" className="text-white">
                                        Dashboard
                                    </Nav.Link>
                                    <Nav.Link href="/home" className="text-white">
                                        Assignment
                                    </Nav.Link>

                                    {user?.is_admin === 1 && (
                                        <>
                                            <Nav.Link href="/allUsers" className="text-white">
                                                All Users
                                            </Nav.Link>
                                            <Nav.Link href="/addUser" className="text-white">
                                                Add User
                                            </Nav.Link>
                                            <Nav.Link href="/questions" className="text-white">
                                                Questions
                                            </Nav.Link>
                                        </>
                                    )}


                                </>
                            )}
                        </Nav>
                        <Nav>
                            {authToken && user ? (
                                <Nav.Link
                                    onClick={handleLogout}
                                    className="text-white"
                                    style={{ cursor: "pointer" }}
                                >
                                    Logout
                                </Nav.Link>
                            ) : (
                                <>
                                    <Nav.Link href="/login" className="text-white">
                                        Login
                                    </Nav.Link>
                                    {/* <Nav.Link href="/register" className="text-white">
                    Register
                  </Nav.Link> */}
                                </>
                            )}
                            <>
                                <Nav.Link href="/users" className="text-white">
                                    All Users
                                </Nav.Link>
                                <Nav.Link href="/addnote" className="text-white">
                                    Add Note
                                </Nav.Link>
                                <Nav.Link href="/addnote" className="text-white">
                                    Add Note
                                </Nav.Link>
                            </>
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
            <div style={{ paddingTop: "56px" }}></div>
        </header>
    );
};

export default Header;
